package test.easy;

import java.util.Set;
import java.util.Arrays;
import java.util.HashSet;

/**
 * @author Kenny Tai Huynh
 * Remove Vowels from a String
 * */
public class RemoveVowelsSolution2 {
	public static void main(String[] args) {
		long start = System.currentTimeMillis();
		System.out.println(removeVowels("kennytaihuynh"));  
		System.out.println(removeVowels("aeiou"));  
		System.out.println(removeVowels(""));  
		System.out.println(removeVowels(""));  
	    System.out.println(removeVowels("apple"));
	    System.out.println(removeVowels("leetcodeisacommunityforcoders"));
	    System.out.println(System.currentTimeMillis() - start);
	}


	public static String removeVowels(String str) {
		char[] chars = str.toCharArray();
		Set<Character> set = new HashSet<>(Arrays.asList('a', 'e', 'i', 'o', 'u'));
		StringBuilder strB = new StringBuilder();
		for(int i = 0; i < chars.length; i++) {
			if(!set.contains(chars[i])) {
				strB.append(chars[i]);
			}
		}
		return strB.toString();
	}
}
